insert  into `animation_information`(`ANIMATION_ID`,`ANIMATION_NAME`,`ANIMATION_ALIAS`,`ANIMATION_EPISODE`,`ANIMATION_BROADCAST_TIME`,`ANIMATION_TYPE`,`ANIMATION_SOURCE`,`ANIMATION_CV`,`ANIMATION_STAFF`,`ANIMATION_INTRO`,`ANIMATION_DETAIL_INFORMATION`,`ANIMATION_REMARK`,`HAVE_POSTER`,`SEQUENCE`,`LAST_UPDATE_TIME`,`HAVE_BD_RESOURCE`) values ('20170819210923463','境界的彼方',NULL,'12','2013-10-02 00:00:00','4','1',NULL,NULL,NULL,NULL,'电视动画《境界的彼方》改编自鸟居なごむ原作的同名轻小说，由石立太一监督率领《日常》原班制作人马打造。故事描述了有特殊能力的异界士少女栗山未来与稀有存在的半妖神原秋人相遇以后发生的一系列不平凡的故事。2013年4月3日，在京都动画旗下KA Esuma文库在官网上，正式宣布《境界的彼方》动画化企划进行中的消息[1]  。动画官网于2013年8月1日上线，并且每天更新一张带有数字的图片进行倒计时，直到8月5日倒计时完毕动画官网方正式公开，此时《境界的彼方》正式确认将以TV动画的形式与观众见面[2-3]  。2013年10月2日起在TOKYO MX首播，全12话。剧场版《境界的彼方 -I\'LL BE HERE-》分为“过去篇”和“未来篇”，分别于2015年3月14日和2015年4月25日在日本上映。','NO',NULL,'2017-08-19 21:09:23','0');
insert  into `animation_information`(`ANIMATION_ID`,`ANIMATION_NAME`,`ANIMATION_ALIAS`,`ANIMATION_EPISODE`,`ANIMATION_BROADCAST_TIME`,`ANIMATION_TYPE`,`ANIMATION_SOURCE`,`ANIMATION_CV`,`ANIMATION_STAFF`,`ANIMATION_INTRO`,`ANIMATION_DETAIL_INFORMATION`,`ANIMATION_REMARK`,`HAVE_POSTER`,`SEQUENCE`,`LAST_UPDATE_TIME`,`HAVE_BD_RESOURCE`) values ('20170819210923575','夏目友人帐OVA',NULL,'2','2013-12-01 00:00:00','5','1','','',NULL,'','《夏目友人帐》（Brain\'s Base制作的系列动画）是根据日本漫画家绿川幸创作的漫画《夏目友人帐》改编的动画作品，夏目友人帐OVA即是发售夏目友人帐DVD、蓝光碟时附赠的原创动画录影带作品，讲述的是从小便能看见妖怪的少年——夏目贵志。自从他从祖母玲子那里继承了与妖怪成为主从，并将其名字书写在上的契约书“友人帐”以来，便与自称为保镖的妖怪猫咪老师一同，开始了将名字返还给妖怪的每一天。夏目与各种各样的妖怪与善良人们相遇，在构筑温暖的场所同时，也反复寄托着想要守护重要之物的想法。','NO',NULL,'2017-08-20 15:35:50','0');
