insert  into `animation_information`(`ANIMATION_ID`,`ANIMATION_NAME`,`ANIMATION_ALIAS`,`ANIMATION_EPISODE`,`ANIMATION_BROADCAST_TIME`,`ANIMATION_TYPE`,`ANIMATION_SOURCE`,`ANIMATION_CV`,`ANIMATION_STAFF`,`ANIMATION_INTRO`,`ANIMATION_DETAIL_INFORMATION`,`ANIMATION_REMARK`,`HAVE_POSTER`,`SEQUENCE`,`LAST_UPDATE_TIME`,`HAVE_BD_RESOURCE`) values ('20170819210928966','水之语',NULL,'1','2003-01-01 00:00:00','1','1',NULL,NULL,NULL,NULL,'《水之语》（水のコトバ） 是六花工作室制作的一部SF短片故事，片长9分钟。故事的原案、脚本以及制作都由吉浦康裕完成，可以说整个影片几乎由他独立完成（音乐·效果音的制作由渡边聡史完成）。是吉浦康裕的早期作品，而他后来的作品，如《苍之茧》（Pale Cocoon）、《夏娃的时间》（イヴの时间），更为出名，也更为大家所熟悉。','NO',NULL,'2017-08-19 21:09:28','0');
insert  into `animation_information`(`ANIMATION_ID`,`ANIMATION_NAME`,`ANIMATION_ALIAS`,`ANIMATION_EPISODE`,`ANIMATION_BROADCAST_TIME`,`ANIMATION_TYPE`,`ANIMATION_SOURCE`,`ANIMATION_CV`,`ANIMATION_STAFF`,`ANIMATION_INTRO`,`ANIMATION_DETAIL_INFORMATION`,`ANIMATION_REMARK`,`HAVE_POSTER`,`SEQUENCE`,`LAST_UPDATE_TIME`,`HAVE_BD_RESOURCE`) values ('20170819210929074','水果篮子',NULL,'26','2001-07-05 00:00:00','3','1',NULL,NULL,NULL,NULL,'电视动画《水果篮子》改编自日本漫画家高屋奈月原作的同名漫画。中国香港的翔群国际、中国台湾的木棉花国际、纬来日本台译作《幻影天使》，而纬来综合台则翻译成《魔法水果篮》，亚洲电视本港台译作《生肖奇缘》。电视动画于2001年开播，由大地丙太郎担任监督，荟集了堀江由衣、久川绫、关智一、置鮎龙太郎、今井由香、三石琴乃、井上和彦等一大批知名声优，是少女漫画动画化的名作之一。','NO',NULL,'2017-08-19 21:09:29','0');
insert  into `animation_information`(`ANIMATION_ID`,`ANIMATION_NAME`,`ANIMATION_ALIAS`,`ANIMATION_EPISODE`,`ANIMATION_BROADCAST_TIME`,`ANIMATION_TYPE`,`ANIMATION_SOURCE`,`ANIMATION_CV`,`ANIMATION_STAFF`,`ANIMATION_INTRO`,`ANIMATION_DETAIL_INFORMATION`,`ANIMATION_REMARK`,`HAVE_POSTER`,`SEQUENCE`,`LAST_UPDATE_TIME`,`HAVE_BD_RESOURCE`) values ('20170819210929183','永生之酒',NULL,'16','2007-07-26 00:00:00','3','1',NULL,NULL,NULL,NULL,'《永生之酒》是轻小说家成田良悟著作，插画家榎波克己负责插画，电击文库所属的轻小说。本作繁体中文版由台湾角川发行，简体中文版版权由天闻角川取得，但未出版实体书，并在腾讯动漫上刊行电子版[1]  。作品亦改编成同名动画和漫画。作品曾获第9届电击游戏小说大奖（在2003年度第11届后改称电击小说大奖）〈金奖〉。“BACCANO”一词在意大利语中有“大骚乱”之意，借以暗示作品中角色与角色间的矛盾以及势力与势力间的冲突。作品中部分人物因为饮用永生之酒而得到了永生，在各个时间，地点，事件中穿插影响，产生了各种错综复杂的故事。','NO',NULL,'2017-08-19 21:09:29','0');
