insert  into `animation_information`(`ANIMATION_ID`,`ANIMATION_NAME`,`ANIMATION_ALIAS`,`ANIMATION_EPISODE`,`ANIMATION_BROADCAST_TIME`,`ANIMATION_TYPE`,`ANIMATION_SOURCE`,`ANIMATION_CV`,`ANIMATION_STAFF`,`ANIMATION_INTRO`,`ANIMATION_DETAIL_INFORMATION`,`ANIMATION_REMARK`,`HAVE_POSTER`,`SEQUENCE`,`LAST_UPDATE_TIME`,`HAVE_BD_RESOURCE`) values ('20170819210931148','目隐都市的演绎者',NULL,'12','2014-04-12 00:00:00','2','1',NULL,NULL,NULL,NULL,'电视动画《目隐都市的演绎者》是日本音乐人、作家Jin主导的跨媒体作品群“阳炎project”的一部分，而Jin本人也在这部动画中担任编剧一职。由SHAFT负责动画制作，2014年4月开播.动画讲述了因某个原因而闭门不出，却在8月14日的一次意外外出中就遭遇到恐怖袭击事件的倒霉高中生如月伸太郎，在寄居在自己手机中的电子少女Ene和一群有着奇怪的“目之能力”的目隐团的成员们的协力下解决事件，并在之后也莫名其妙地被作为这个团体的一员而展开的都市奇幻故事。动画与Jin著作的轻小说《阳炎眩乱》一样都是以“阳炎Project”组曲为基础，但剧情走向已产生了微妙的不同。动画从2014年4月12日起在日本各大电视台开播。动画的首播电视台TOKYO MX的更新时间为每周六深夜24:00（北京时间23:00），至6月28日，全12话放送完结。除了在电视上播出，动画还在niconico动画等视频网站上进行网络播放。','NO',NULL,'2017-08-19 21:09:31','0');
insert  into `animation_information`(`ANIMATION_ID`,`ANIMATION_NAME`,`ANIMATION_ALIAS`,`ANIMATION_EPISODE`,`ANIMATION_BROADCAST_TIME`,`ANIMATION_TYPE`,`ANIMATION_SOURCE`,`ANIMATION_CV`,`ANIMATION_STAFF`,`ANIMATION_INTRO`,`ANIMATION_DETAIL_INFORMATION`,`ANIMATION_REMARK`,`HAVE_POSTER`,`SEQUENCE`,`LAST_UPDATE_TIME`,`HAVE_BD_RESOURCE`) values ('20170819210931257','眼镜部',NULL,'12','2013-10-06 00:00:00','4','1','','',NULL,'','2013年10月开播的电视动画《眼镜部！》由MEGANEBU! Project原作，Studio DEEN担当动画制作，山本苍美监督的原创动画作品。故事围绕着以相马镜为首的眼镜部的五个男子高中生之间的青春物语，以及他们对眼镜的真爱宣言展开。','NO',NULL,'2017-08-20 21:08:33','0');
insert  into `animation_information`(`ANIMATION_ID`,`ANIMATION_NAME`,`ANIMATION_ALIAS`,`ANIMATION_EPISODE`,`ANIMATION_BROADCAST_TIME`,`ANIMATION_TYPE`,`ANIMATION_SOURCE`,`ANIMATION_CV`,`ANIMATION_STAFF`,`ANIMATION_INTRO`,`ANIMATION_DETAIL_INFORMATION`,`ANIMATION_REMARK`,`HAVE_POSTER`,`SEQUENCE`,`LAST_UPDATE_TIME`,`HAVE_BD_RESOURCE`) values ('20170819210931364','神之谜题_第3季',NULL,'25','2013-10-06 00:00:00','4','1',NULL,NULL,NULL,NULL,'神之谜题第三季 在Orpheus Order的战斗后，新的对手莱赛尔登场。围绕着曾经挑战“神之谜题”的真方仁展开，由于真方仁和毕达哥拉斯伯爵的对战导致失去了心智。目前的仁只恢复了意识，记忆仍然缺失，为了寻找仁的记忆，以及真实的仁。大门界人和同伴遭遇了同样与仁过去有关的女孩莱赛尔。为了寻找心目中的真实的仁，展开了新一轮的谜题较量，期间还有神秘组织Master Brain及它的统领艾尼格玛幕后推动，真相到底是什么呢？','NO',NULL,'2017-08-19 21:09:31','0');
