insert  into `animation_information`(`ANIMATION_ID`,`ANIMATION_NAME`,`ANIMATION_ALIAS`,`ANIMATION_EPISODE`,`ANIMATION_BROADCAST_TIME`,`ANIMATION_TYPE`,`ANIMATION_SOURCE`,`ANIMATION_CV`,`ANIMATION_STAFF`,`ANIMATION_INTRO`,`ANIMATION_DETAIL_INFORMATION`,`ANIMATION_REMARK`,`HAVE_POSTER`,`SEQUENCE`,`LAST_UPDATE_TIME`,`HAVE_BD_RESOURCE`) values ('20170820203123637','萌菌物语_第2季',NULL,'11','2012-07-05 00:00:00','3','1','','',NULL,'拥有用肉眼看见“菌”能力的少年泽木直保和青梅竹马结城萤进入农业大学所发生一连串的趣事，本作清新可爱，搞笑的同时也轻松的介绍了很多科普知识，各种“菌”不仅造型超萌，而且有自己的思想，还可以说话 。','','NO',NULL,'2017-08-20 20:31:23','0');
insert  into `animation_information`(`ANIMATION_ID`,`ANIMATION_NAME`,`ANIMATION_ALIAS`,`ANIMATION_EPISODE`,`ANIMATION_BROADCAST_TIME`,`ANIMATION_TYPE`,`ANIMATION_SOURCE`,`ANIMATION_CV`,`ANIMATION_STAFF`,`ANIMATION_INTRO`,`ANIMATION_DETAIL_INFORMATION`,`ANIMATION_REMARK`,`HAVE_POSTER`,`SEQUENCE`,`LAST_UPDATE_TIME`,`HAVE_BD_RESOURCE`) values ('20170820205533523','圣痕炼金士_第2季',NULL,'12','2011-04-11 00:00:00','2','1','','',NULL,'炼金士可以自由自在地操纵特定元素的特殊能力者。他们常在世界历史的暗处行动。就读私立米海洛夫学园的织部真冬和山边灯，从邂逅称为沙夏的俄罗斯人后开始卷入了炼金士们的战斗……\r\n圣痕炼金士是由吉野弘幸创作、佐藤健悦作画的校园格斗漫画，从「Champion RED」2006年10月号开始连载，至今已经发行了7卷单行本，并曾于2007年9月Drama化。原作的画风是华丽绚烂到眼花缭乱的程度，将于 2010年1月开播的动画版是由新社Fu\"s Entertainment制作，曾在「灼眼的夏娜」「神样中学生」中担任作画监督以及「女王之刃」中负责人设的金子ひらく，将出在本作中首次担当动画监督一职，系列构成・剧本是曾负责「传颂之物」「School Days」「濑户的花嫁」的上江洲诚，人设是「魔女之刃」「纯情房东俏房客」「宇宙星路」的うのまこと。CAST方面，有着三瓶由布子、藤村步、丰崎爱生等当红女声优的参演。','','NO',NULL,'2017-08-20 20:55:33','0');
insert  into `animation_information`(`ANIMATION_ID`,`ANIMATION_NAME`,`ANIMATION_ALIAS`,`ANIMATION_EPISODE`,`ANIMATION_BROADCAST_TIME`,`ANIMATION_TYPE`,`ANIMATION_SOURCE`,`ANIMATION_CV`,`ANIMATION_STAFF`,`ANIMATION_INTRO`,`ANIMATION_DETAIL_INFORMATION`,`ANIMATION_REMARK`,`HAVE_POSTER`,`SEQUENCE`,`LAST_UPDATE_TIME`,`HAVE_BD_RESOURCE`) values ('20170820210142646','妄想学生会_第2季',NULL,'13','2014-01-04 00:00:00','1','1','','',NULL,'电视动画《妄想学生会*》改编自日本漫画家氏家ト全原作的漫画《妄想学生会》，亦是电视动画《妄想学生会》的第2期作品。在《周刊少年Magazine》2013年46号（10月16日发售）发表了《妄想学生会*》的制作消息[1-2]  。电视动画于2014年1月4日开始播放。OAD第14话、15话分别于2014年5月16日（漫画第10卷特装版同捆DVD）、2014年10月22日（《生徒会役员共* OVA》[3]  ）。OAD第16~18话在漫画第11~13卷特装版同捆DVD中发售（2015年2月17日、2015年9月17日和2016年4月15日）。OAD第19话在漫画第14卷特装版同捆DVD中于2016年12月16日发售。','','NO',NULL,'2017-08-20 21:01:42','0');
